import { useState, useEffect } from "react";
import axios from "axios";
import DisplayApplicationsChild from "./DisplayApplicationsChild";
// import CreateApplicationModal from "../CreateApplication/CreateApplicationModal";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
// import Add from "@material-ui/icons/Add";
// import "./DisplayApplicationsStyles.css";


// import AppBar from '@material-ui/core/AppBar';
// import "./PrivateScreen.css";

const useStyles = makeStyles(theme => ({
    button: {
        margin: theme.spacing(1),
        borderRadius: "5em"
    },
    createApplicationButton: {
        borderRadius: "50%",
        position: "fixed",
        right: "5em",
        bottom: "4em",
        height: "60px",
        widht: "60px",
        fontSize: "30px"
    },
    input: {
        display: "none"
    }
}));

const Dashboard = ({ history }) => {
    const [error, setError] = useState("");
    const [applications, setApplications] = useState("");
    const classes = useStyles();

    useEffect(() => {
        const fetchApplications = async () => {
            const config = {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("authToken")}`,
                },
            };

            try {
                const { data } = await axios.get("/api/applications/displayApplications", config);
                setApplications(data.userApplications);
            } catch (error) {
                localStorage.removeItem("authToken");
                setError("You are not authorized please login");
            }
        };

        fetchApplications();
    }, []);

    const logoutHandler = () => {
        localStorage.removeItem("authToken");
        history.push("/login");
    }

    const createApplicationHandler = () => {
        // localStorage.removeItem("authToken");
        history.push("/createApplication");
    }

    return error ? (
        <span className="error-message">{error}</span>
    ) : (
        <>
            {/* <div style={{ background: "green", color: "white" }}>{applications}</div> */}
            <DisplayApplicationsChild applications={applications} />
            {/* <div>
                <tr>
                    <td>{applications[0].company}</td>
                    <td>{applications[0].position}</td>
                    <td>{applications[0].status}</td>
                </tr>
            </div> */}
            {/* <button onClick={logoutHandler}>Logout</button> */}
            <Button variant="outlined" color="secondary" onClick={logoutHandler} className="logout-btn">Logout</Button>
            <Button variant="contained" color="primary" onClick={createApplicationHandler} className={classes.createApplicationButton}>
                {/* <Add></Add> */}
                +
            </Button>
        </>
    );
};

export default Dashboard;
