import * as React from 'react';
import Box from '@material-ui/core/Box';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const DisplayApplicationsChild = (props) => {

    const displayAplications = (props) => {
        const { applications } = props;

        if (applications.length > 0) {
            return (
                applications.map((application, index) => {
                    console.log(application);
                    return (
                        // <div>
                        //     <h3>{application.company}</h3>
                        //     <p>{application.position}</p>
                        //     <p>{application.status}</p>
                        // </div>
                        <Box component="span" sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }} >
                            <React.Fragment>
                                <CardContent>
                                    <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                                        {application.company}
                                    </Typography>
                                    <Typography variant="h5" component="div">
                                        {application.position}}
                                    </Typography>
                                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                                        `{application.status} + Date Applied: {application.dateApplied}`
                                    </Typography>
                                    <Typography variant="body2">
                                        {application.comments}
                                    </Typography>
                                </CardContent>
                                {/* <CardActions>
                                    <Button size="small">Learn More</Button>
                                </CardActions> */}
                            </React.Fragment>
                        </Box>
                    )
                })
            )
        } else {
            return (<h3>No applications yet</h3>)
        }
    }
    return (
        <>
            {displayAplications(props)}
        </>
    )
}

export default DisplayApplicationsChild
