import * as React from 'react';
import Box from '@material-ui/core/Box';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Typography from '@material-ui/core/Typography';
import Loading from './Loading';
import moment from 'moment';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import BusinessIcon from '@material-ui/icons/Business';
import axios from 'axios';
import Avatar from '@material-ui/core/Avatar';

const DisplayApplicationsChild = (props) => {

    const displayAplications = (props) => {
        const { applications } = props;
        // const [companyLogo, setCompanyLogo] = useState("");
        // const [companyName, setCompanyName] = useState("");

        if (applications.length > 0) {
            return (
                applications.map((application, index) => {
                    // console.log(application);

                    const formattedDate = moment(application.dateApplied).format('MM-DD-YY');

                    const companyLogoData = `https://autocomplete.clearbit.com/v1/companies/suggest?query=${application.company}`;
                    const companyLogo = companyLogoData[0].logo;

                    // useEffect(() => {
                    //     const getCompanyLogo = async () => {
                    //         try {
                    //             const data = await axios.get(
                    //                 `https://autocomplete.clearbit.com/v1/companies/suggest?query=${application.company}`
                    //             );
                    //             setCompanyLogo(data.logo);
                    //             setCompanyName(data.name);
                    //         } catch (error) {
                    //             setCompanyLogo(BusinessIcon);
                    //         }
                    //     };
                    //     getCompanyLogo();
                    // }, [])

                    return (
                        <Box component="span" style={{ display: 'inline-block', transform: 'scale(0.8)', minWidth: 275, padding: '20px' }} >
                            <React.Fragment>
                                <Card variant="outlined" style={{ height: "250px", width: "300px" }}>
                                    <CardHeader
                                        action={
                                            <IconButton color="primary" >
                                                <MoreVertIcon fontSize="large" />
                                            </IconButton>
                                        }
                                        avatar={
                                            <Avatar image={companyLogo} aria-label={`company-logo`} />
                                        }
                                        title={application.company}
                                        subheader={`Applied on: ${formattedDate}`}
                                    />
                                    <CardContent>

                                        {/* <Typography style={{ fontSize: 21, fontWeight: 600 }} gutterBottom>
                                            {application.company}
                                        </Typography> */}
                                        <Typography variant="h5" component="div" style={{ fontColor: "" }}>
                                            {application.position}
                                        </Typography>
                                        <Typography style={{ mb: 1.5 }} color="text.secondary">
                                            {application.status}
                                        </Typography>
                                        <Typography variant="body2">
                                            {application.comments}
                                        </Typography>
                                    </CardContent>
                                </Card>
                            </React.Fragment>
                        </Box>
                    )
                })
            )
        } else {
            // return (<h3>No applications yet</h3>)
            <Loading size={10} />
        }
    }
    return (
        <>
            {displayAplications(props)}
        </>
    )
}


export default DisplayApplicationsChild
