import { useState, useEffect } from "react";
import axios from "axios";
// import "./PrivateScreen.css";

const ApplicationsDisplayScreen = ({ history }) => {
    const [error, setError] = useState("");
    const [applications, setApplications] = useState("");

    useEffect(() => {
        const fetchApplications = async () => {
            const config = {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("authToken")}`,
                },
            };

            try {
                const { data } = await axios.get("http://localhost:5000/api/applications/displayApplications", config);
                setApplications(data.userApplications);
                console.log(applications);
            } catch (error) {
                localStorage.removeItem("authToken");
                setError("You are not authorized please login");
            }
        };

        fetchApplications();
    }, [applications]);

    const logoutHandler = () => {
        localStorage.removeItem("authToken");
        history.push("/login");
    }

    return error ? (
        <span className="error-message">{error}</span>
    ) : (
        <>
            {/* <div style={{ background: "green", color: "white" }}>{privateData}</div> */}
            <div>
                {applications.map(application => <div>{application.username}</div>)}
            </div>
            <button onClick={logoutHandler}>Logout</button>
        </>
    );
};

export default ApplicationsDisplayScreen;
